import React, { useState } from 'react';
import ExpenseForm from './ExpenseForm.js';

import './NewExpense.css';

const NewExpense = function(props) {
  const saveExpenseDataHandler = (enteredExpenseData) => {
    const expenseData = {
      ...enteredExpenseData,
      id: Math.random().toString(),
    }
    props.onAddExpense(expenseData);
  }

  const [form, setForm] = useState('');

  const toggleFormHandler = function() {
    if (form === '') {
      setForm(<ExpenseForm onSaveExpenseData={saveExpenseDataHandler} />);
    } else {
      setForm('');
    }
  }

  const closeFormHandler = function() {

  }

  return (
    <div className='new-expense'>
      <button onClick={toggleFormHandler}>Add New Expense</button>
      {form}
    </div>
  );
}

export default NewExpense;
