import React, { useState } from 'react';
import ExpenseForm from './ExpenseForm.js';

import './NewExpense.css';

const NewExpense = function(props) {
  const [isEditing, setIsEditing] = useState(false);

  const saveExpenseDataHandler = (enteredExpenseData) => {
    const expenseData = {
      ...enteredExpenseData,
      id: Math.random().toString(),
    }
    props.onAddExpense(expenseData);
  }

  const startEditingHandler = function() {
    setIsEditing(true);
  }

  const closeFormHandler = function() {
    setForm('');
    setBtnAdd(<button onClick={openFormHandler}>Add New Expense</button>);
  }

  const openFormHandler = function() {
    setForm(<ExpenseForm onCloseForm={closeFormHandler} onSaveExpenseData={saveExpenseDataHandler} />);
    setBtnAdd('');
  }

  const [form, setForm] = useState('');

  const [btnAdd, setBtnAdd] = useState
  (
    <button onClick={openFormHandler}>Add New Expense</button>
  );

  // const [form, setForm] = useState('');


  return (
    <div className='new-expense'>
      {!isEditing && <button onClick={openFormHandler}>Add New Expense</button>}
      {isEditing && <ExpenseForm onCloseForm={closeFormHandler} onSaveExpenseData={saveExpenseDataHandler} />}
      {}
    </div>
  );
}

export default NewExpense;
